mapper.select:
select * from tb_leave
where state='请假同意'


    and name like #{leave.name}



    and reason like #{leave.reason}



    and (start_date like #{leave.startDate} or last_date like #{leave.lastDate})


limit #{begin},#{size}

select count(*) from tb_leave
where state='请假同意'


    and name like #{name}



    and reason like #{reason}



    and (start_date like #{startDate} or last_date like #{lastDate})

select * from tb_leave
where state='请假同意' and user_id=#{leave.userId}


    and reason like #{leave.reason}



    and (start_date like #{leave.startDate} or last_date like #{leave.lastDate})


limit #{begin},#{size}

select count(*) from tb_leave
where state='请假同意' and user_id=#{userId}


    and reason like #{reason}



    and (start_date like #{startDate} or last_date like #{lastDate})

select * from tb_leave where user_id = #{userId}
and(state = '请假申请' or state = '请假拒绝')
and state = '离职申请'
